import React from 'react'
import styled from "styled-components"
import bg from "../assets/bg.png"
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useHistory } from 'react-router';
import { useForm } from 'react-hook-form';



const Login = () => {
    const history = useHistory()
    const { register, handleSubmit, errors } = useForm();

    const handleRegistration = (data) => console.log(data);
    const handleError = (errors) => { console.log};
    const registerOptions = {
        email: { required: "Email is required" },
        password: {
          required: "Password is required",
          minLength: {
            value: 8,
            message: "Password must have at least 8 characters"
          }
        }
    };


    return (
        <Container>
        <BackDrop>
        <Logo>
                    <Astra>AstraKash</Astra>
                    <Welcome>Welcome to Astrakash Limited</Welcome>
        </Logo>
         <FormContent>
                    <FormInput>
                        <SignIn>Sign in</SignIn>
                    <p style={{marginTop: 20}}>
                    Manage users, plans stream and more on SpurtFlix movie streaming platform.
                        </p>
                        <div style={{width:250, height:2, background:'#1742D9', marginTop:20}}>
                        </div><br /><br />
                        <form onSubmit={handleSubmit(handleRegistration, handleError)}>
                        <label>
                        Email/Username
                        </label><br/><br/>
                        <TextField id="outlined-basic" label="Email/Username" variant="outlined" style={{ width: '100%' }} {...register('email', registerOptions.email)}/>
                            <br /><br />
                            <small className="text-danger">
          {errors.email && errors.email.message}
        </small>
                        <label>
                        Password
                    </label><br/><br/>
                            <TextField id="outlined-basic" label="Password" variant="outlined" style={{ width: '100%' }} {...register('password', registerOptions.password)} />
                             <small className="text-danger">
          {errors.password && errors.password.message}
        </small>
                            <Button variant="contained" style={{width:'100%', height: 50, marginTop: 60}} onClick={()=>history.push('/dashboard')}>Sign In</Button>
                        </form>
        </FormInput>
         </FormContent>
        </BackDrop>
        </Container>
    )
}


const SignIn = styled.div`
font-size: 20px;
color:#1742D9;
font-weight: bold;
font-size: 24px;
`;

const FormInput = styled.div`
width: 500px;
height: 600px;
background:#ffffff;
border-radius: 24px;
padding: 60px 40px 30px 40px;
`;

const Container = styled.div`
background-position: 0 0;
background-repeat: no-repeat;
width: 100%;
background-image: url(${bg});
background-size: cover;
`;


const BackDrop = styled.div`
width: 100%;
background: rgba(23, 66, 217, 0.7);
display:flex;
justify-content: space-around;
align-items: center;
`;


const Logo = styled.div`
width: 35%;
height:700px;
display:flex;
flex-direction:column;
justify-content:space-between;
`;

const FormContent = styled.div`
width: 45%;
height:100vh;
display:flex;
justify-content:center;
align-items:center;
`;


const Astra = styled.div`
font-size: 32px;
font-weight:700;
color:#ffffff;
`;

const Welcome = styled.div`
font-size: 48px;
font-weight:700;
color:#ffffff;
`;
export default Login
